   // ============ insert this code inside main() ==============#include <iostream>#include <cassert>#include <limits>using namespace std;     int main()  {    short j, oddSum, N;    cout << "Enter a value for N: ";    cin >> N;      j      =    1;    oddSum =    0;    while(j < N && N <= 101)   // INV: oddSum == 1 + 3 + ... + (j-2)  &&  1 <= j <= 101                    //									 &&  j%2 == 1    {      oddSum = oddSum + j;      cout << "odd sum: " << oddSum << endl;      j = j + 2;      cout << " j: " << j << endl;     	  // do NOT change or move this assertion     	assert( (1<=j && j<=101) && (j%2 == 1) );  	     	   // if assert doesn't work  :(  	   // then uncomment the following:  	       if (! ((1<=j && j<=101) && (j%2 == 1))  )  {      cout << "ASSERT FAILED!" << endl;      system("PAUSE");      exit(-1);    }           	        		     		     		     		     		     }          // ASSERT:  (you write this one HERE as a COMMENT, *not* a runtime assert() )          cout << "\n\n oddSum is: " << oddSum << endl;   }